import java.time.LocalDateTime; import java.time.format.DateTimeFormatter; public class TestAccountGeneration { public static void main(String[] args) { String today = LocalDateTime.now().format(DateTimeFormatter.ofPattern("yyyyMMdd")); System.out.println("새로운 본사 계정번호 패턴 테스트"); System.out.println("날짜: " + today); for (int i = 0; i < 5; i++) { int sequence = 1700 + i; String accountNumber = String.format("%s%d", today, sequence); System.out.println((i+1) + "번째 본사: " + accountNumber); } } }
